import numpy as npdef planetInit():    return planetCoordInit(), planetVelsInit()def planetCoordInit():    return np.random.rand(2)*2-1def planetVelsInit():    return 0.01*(np.random.rand(2)*2-1)def VelocityVerlet(planetCoords, planetVels, G):    Acce_n_1  = calcGravitationalAccel(planetCoords,G)    planetCoords += 0.5*Acce_n_1 + planetVels    planetVels   += 0.5*(calcGravitationalAccel(planetCoords,G) + Acce_n_1)     return planetCoords, planetVelsdef calcGravitationalAccel(x,G):    return vectorDir(x)*G/vectorMag(x)**2def vectorMag(x):    return np.sqrt(np.sum(np.square(x)))def vectorDir(x):    return -x/vectorMag(x)    